class Solution:
    def minSubArrayLen(self, target: int, nums) -> int:
        print(nums)
        res = len(nums)+1
        curr, begin = 0, 0
        for i in range(len(nums)):
            curr += nums[i]
            
            
                
            while curr>=target:
                res = min(res, i-begin+1)
                curr -= nums[begin]
                begin += 1
        if res==len(nums)+1:
            return 0
        return res


